eros(l,nD);","    for i=1:nD","        c(:,i) = B_s_cross * f(:,i);","    end","else","    nD = size(f,2);","    c = zeros(l,nD);","    for i=1:nD","        c(:,i) = Ktmp \\ f(:,i);","    end","end","","clear Ktmp B_s_cross;","","%% step 4","f_s = zeros(n,nD);","for i=1:nD","    f_s(:,i) = B_s * c(:,i);","end","","%% step 5","p = size(x_star,1);","f_star_s = zeros(p,nD);","","for j=1:p","    tmp = repmat(x_star(j,:) , l , 1);","    tmp = (tmp - x_s).^2;","    tmp = sum(tmp,2);","    if( NYSTROM == 0 )","        G_star_s = exp( -tmp / e_s );  %% (l_s x 1)","    else","        G_star_s = exp( -tmp / (2*e_s.^2) );  %% (l_s x 1)","    end","","%% step 6","    f_star_s(j,:) = G_star_s' * c(:,:);","end","","output_03.f_s = f_s;","output_03.f_star_s = f_star_s;","","end"],"stylingDirectives":[[{"start":0,"end":8,"cssClass":"pl-k"},{"start":9,"end":18,"cssClass":"pl-v"},{"start":19,"end":20,"cssClass":"pl-k"},{"start":21,"end":62,"cssClass":"pl-en"},{"start":64,"end":67,"cssClass":"pl-v"},{"start":70,"end":71,"cssClass":"pl-v"},{"start":74,"end":77,"cssClass":"pl-v"},{"start":80,"end":86,"cssClass":"pl-v"},{"start":89,"end":92,"cssClass":"pl-v"},{"start":95,"end":100,"cssClass":"pl-v"},{"start":103,"end":109,"cssClass":"pl-v"},{"start":112,"end":119,"cssClass":"pl-v"},{"start":122,"end":129,"cssClass":"pl-v"}],[],[{"start":1,"end":2,"cssClass":"pl-smi"},{"start":3,"end":4,"cssClass":"pl-smi"},{"start":6,"end":7,"cssClass":"pl-k"},{"start":8,"end":12,"cssClass":"pl-en"},{"start":13,"end":16,"cssClass":"pl-smi"}],[],[{"start":0,"end":2,"cssClass":"pl-k"},{"start":5,"end":6,"cssClass":"pl-smi"},{"start":7,"end":9,"cssClass":"pl-k"},{"start":10,"end":11,"cssClass":"pl-smi"},{"start":13,"end":15,"cssClass":"pl-k"},{"start":17,"end":18,"cssClass":"pl-smi"},{"start":19,"end":21,"cssClass":"pl-k"},{"start":22,"end":28,"cssClass":"pl-en"},{"start":29,"end":35,"cssClass":"pl-smi"}],[{"start":8,"end":9,"cssClass":"pl-k"},{"start":10,"end":13,"cssClass":"pl-en"},{"start":14,"end":15,"c